#home-page-skeleton { position: relative; z-index: 0; pointer-events: none; min-width: 0px; width: 100%; opacity: 1; margin: 56px 0px 0px; display: flex; -webkit-box-orient: horizontal; -webkit-box-direction: normal; flex-direction: row; }
