#guide-skeleton { display: none; background-color: rgb(255, 255, 255); width: 240px; flex-shrink: 0; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; }
#guide-skeleton.collapsed { width: 72px; }
