#guide-skeleton .guide-ghost { padding: 10px 30px; display: flex; }
#guide-skeleton .guide-ghost-icon { height: 40px; width: 40px; min-width: 40px; margin-right: 15px; border-radius: 50%; background-color: rgb(255, 255, 255); }
#guide-skeleton .guide-ghost-text { height: 36px; width: 100%; border-radius: 8px; background-color: rgb(255, 255, 255); }
@media (min-width: 792px) {
  #home-page-skeleton #guide-skeleton { display: flex; }
}
@media (min-width: 792px) and (max-width: 1311px) {
  #home-page-skeleton #guide-skeleton { width: 72px; }
}
#home-container-skeleton { background-color: rgb(255, 255, 255); width: 100%; overflow: hidden; }
#home-page-skeleton .text-shell { border-radius: 2px; }
