html[dark] #home-page-skeleton .skeleton-bg-color { background-color: rgb(41, 41, 41); }
html[dark] #guide-skeleton { background-color: rgb(15, 15, 15); }
html[dark] #home-container-skeleton { background-color: rgb(15, 15, 15); }
html[dark] { scrollbar-color: rgb(171, 171, 171) transparent; }
body::-webkit-scrollbar { width: 16px; }
body::-webkit-scrollbar-thumb { height: 56px; border-radius: 8px; border: 4px solid transparent; background-clip: content-box; background-color: rgb(171, 171, 171); }
#home-container-media { border-bottom: none; margin: 18px auto 0px; padding: 0px 16px; display: flex; -webkit-box-orient: horizontal; -webkit-box-direction: normal; flex-flow: wrap; -webkit-box-pack: center; justify-content: center; }
@media only screen and (max-width: 600px) {
  #home-container-media { padding: 0px; margin-left: -8px; margin-right: -8px; }
}
#home-chips { background: rgba(255, 255, 255, 0.98); height: 56px; border-top: 1px solid rgba(0, 0, 0, 0.1); border-bottom: 1px solid rgba(0, 0, 0, 0.1); display: flex; padding: 12px 0px 0px 24px; }
#home-chips.more-ghost-items { border-top: 0px; border-bottom: 0px; }
#home-chips .home-chips-ghost { height: 32px; width: 80px; border-radius: 8px; margin-right: 20px; }
html[darker-dark-theme] #home-chips { background-color: rgb(255, 255, 255); height: 56px; }
html[dark] #home-chips { background-color: rgb(15, 15, 15); border-top-color: rgba(255, 255, 255, 0.1); border-bottom-color: rgba(255, 255, 255, 0.1); }
#home-page-skeleton .rich-grid-media-skeleton { margin: 0px; flex-basis: 310px; min-width: 310px; max-width: 700px; -webkit-box-flex: 1; flex-grow: 1; }
@media only screen and (min-width: 1600px) {
  #home-container-media { display: grid; grid-template-columns: repeat(3, 1fr); }
  #home-page-skeleton .rich-grid-media-skeleton { margin: 0px 8px; width: auto; max-width: none; -webkit-box-flex: unset; flex-grow: unset; flex-basis: unset; }
}
@media only screen and (min-width: 392px) {
  #home-page-skeleton .rich-grid-media-skeleton { margin: 0px 8px; }
}
#home-page-skeleton .video-details { padding-bottom: 40px; display: flex; -webkit-box-orient: vertical; -webkit-box-direction: normal; flex-direction: column; }
#home-page-skeleton .rich-thumbnail { border-radius: 8px; position: relative; width: 100%; }
