#home-page-skeleton .details { display: flex; }
#home-page-skeleton .channel-avatar { border-radius: 50%; height: 36px; margin-top: 12px; margin-right: 12px; width: 36px; }
#home-page-skeleton .details-text-shell { -webkit-box-flex: 1; flex: 1 1 auto; }
#home-page-skeleton .rich-video-title { width: 90%; height: 20px; margin: 10px 0px; }
